import React from 'react'
function Heading(props){
const style={
color:" #3B3B3B",
fontSize:`${props.size}`,
fontFamily:"'PT Serif', serif",
textAlign:"center",
margin:"1px",
marginBottom:"10px"


}
return(
    <h1 style={style}>{props.text}</h1>
)



}
export default Heading;
